--Rank() & dense_rank()use googleselect * from employeeselect *, rank() over(order by salary desc) as Rank_salary from employeeselect *, rank() over(order by name) as Name_rank from employeeSELECT *, dense_rank() OVER (ORDER BY salary DESC) AS dense_rank_sal from employee--we can only put rank and dense_rank over a column which can be sorted.SELECT *, RANK() over (Order by salary desc) as Rank_sal, dense_rank() over(order by salary desc) as dense_rank_sal from employee